// ===== CATEGORIES =====
const CATEGORIES = [
    "All Products",
    "Kitchen Products",
    "Electronics",
    "Home Essentials",
    "Beauty & Health",
    "Fashion"
];

// ===== STORAGE FUNCTIONS =====
function initializeStorage() {
    if (!localStorage.getItem('affiliateProducts')) {
        localStorage.setItem('affiliateProducts', JSON.stringify(INITIAL_PRODUCTS));
    }
    loadProducts();
}

function loadProducts() {
    const stored = localStorage.getItem('affiliateProducts');
    APP_STATE.products = stored ? JSON.parse(stored) : INITIAL_PRODUCTS;
    APP_STATE.filteredProducts = [...APP_STATE.products];
}

function saveProducts() {
    localStorage.setItem('affiliateProducts', JSON.stringify(APP_STATE.products));
}

// ===== AUTHENTICATION =====
function checkAuth() {
    return localStorage.getItem('isAdminLoggedIn') === 'true';
}

function login(username, password) {
    // Simple authentication (in production, use secure backend)
    if (username === 'admin' && password === 'admin123') {
        localStorage.setItem('isAdminLoggedIn', 'true');
        APP_STATE.isAdmin = true;
        return true;
    }
    return false;
}

function logout() {
    localStorage.removeItem('isAdminLoggedIn');
    APP_STATE.isAdmin = false;
    navigateTo('home');
}

// ===== NAVIGATION =====
function navigateTo(page, productId = null) {
    APP_STATE.currentPage = page;

    // Update active nav link
    document.querySelectorAll('.nav-menu a').forEach(link => {
        link.classList.remove('active');
    });

    const navMap = {
        'home': 'navHome',
        'categories': 'navCategories',
        'about': 'navAbout',
        'blog': 'navBlog',
        'contact': 'navContact',
        'admin': 'navAdmin'
    };

    const activeNav = document.getElementById(navMap[page]);
    if (activeNav) activeNav.classList.add('active');

    // Render appropriate page
    switch(page) {
        case 'home':
            renderHomePage();
            break;
        case 'product':
            renderProductDetail(productId);
            break;
        case 'about':
            renderAboutPage();
            break;
        case 'blog':
            renderBlogPage();
            break;
        case 'contact':
            renderContactPage();
            break;
        case 'admin':
            if (checkAuth()) {
                renderAdminDashboard();
            } else {
                renderAdminLogin();
            }
            break;
        case 'privacy':
            renderPrivacyPage();
            break;
        case 'disclaimer':
            renderDisclaimerPage();
            break;
        case 'terms':
            renderTermsPage();
            break;
        default:
            render404Page();
    }

    // Close mobile menu
    document.getElementById('navMenu').classList.remove('active');

    // Scroll to top
    window.scrollTo(0, 0);
}

// ===== HOME PAGE =====
function renderHomePage() {
    const app = document.getElementById('mainApp');

    app.innerHTML = `
        <div class="main-content">
            <div class="search-container">
                <div class="container">
                    <div class="search-bar">
                        <input type="text" class="search-input" id="searchInput" placeholder="Search products...">
                        <span class="search-icon">üîç</span>
                    </div>
                </div>
            </div>

            <div class="container">
                <div class="content-grid">
                    <div class="product-feed">
                        <div class="products-grid" id="productsGrid"></div>
                        <div class="pagination" id="pagination"></div>
                    </div>

                    <aside class="sidebar">
                        <div class="sidebar-section">
                            <h3 class="sidebar-title">Categories</h3>
                            <ul class="category-list" id="categoryList"></ul>
                        </div>
                    </aside>
                </div>
            </div>
        </div>
    `;

    renderCategories();
    renderProducts();
    setupSearch();
}

function renderCategories() {
    const categoryList = document.getElementById('categoryList');
    if (!categoryList) return;

    categoryList.innerHTML = CATEGORIES.map(cat => `
        <li class="category-item ${APP_STATE.currentCategory === cat.toLowerCase().replace(/\s+/g, '-') || (cat === 'All Products' && APP_STATE.currentCategory === 'all') ? 'active' : ''}" 
            onclick="filterByCategory('${cat.toLowerCase().replace(/\s+/g, '-')}')">
            ${cat}
        </li>
    `).join('');
}

function renderProducts() {
    const grid = document.getElementById('productsGrid');
    if (!grid) return;

    // Apply filters
    let filtered = [...APP_STATE.products];

    if (APP_STATE.currentCategory !== 'all') {
        const catName = APP_STATE.currentCategory.replace(/-/g, ' ');
        filtered = filtered.filter(p => 
            p.category.toLowerCase() === catName
        );
    }

    if (APP_STATE.searchQuery) {
        const query = APP_STATE.searchQuery.toLowerCase();
        filtered = filtered.filter(p =>
            p.title.toLowerCase().includes(query) ||
            p.shortDescription.toLowerCase().includes(query) ||
            p.category.toLowerCase().includes(query)
        );
    }

    APP_STATE.filteredProducts = filtered;

    // Pagination
    const start = (APP_STATE.currentPage - 1) * APP_STATE.productsPerPage;
    const end = start + APP_STATE.productsPerPage;
    const paginatedProducts = filtered.slice(start, end);

    if (paginatedProducts.length === 0) {
        grid.innerHTML = '<div class="loading">No products found</div>';
        return;
    }

    grid.innerHTML = paginatedProducts.map(product => `
        <div class="product-card fade-in" onclick="navigateTo('product', ${product.id})">
            <img src="${product.image}" alt="${product.title}" class="product-image" loading="lazy">
            <div class="product-info">
                <span class="product-category">${product.category}</span>
                <h3 class="product-title">${product.title}</h3>
                <p class="product-description">${product.shortDescription}</p>
                <button class="btn btn-secondary">View Details</button>
            </div>
        </div>
    `).join('');

    renderPagination();
}

function renderPagination() {
    const pagination = document.getElementById('pagination');
    if (!pagination) return;

    const totalPages = Math.ceil(APP_STATE.filteredProducts.length / APP_STATE.productsPerPage);

    if (totalPages <= 1) {
        pagination.innerHTML = '';
        return;
    }

    let html = '';

    if (APP_STATE.currentPage > 1) {
        html += `<button class="page-btn" onclick="changePage(${APP_STATE.currentPage - 1})">Previous</button>`;
    }

    for (let i = 1; i <= totalPages; i++) {
        html += `<button class="page-btn ${i === APP_STATE.currentPage ? 'active' : ''}" onclick="changePage(${i})">${i}</button>`;
    }

    if (APP_STATE.currentPage < totalPages) {
        html += `<button class="page-btn" onclick="changePage(${APP_STATE.currentPage + 1})">Next</button>`;
    }

    pagination.innerHTML = html;
}

function changePage(page) {
    APP_STATE.currentPage = page;
    renderProducts();
    window.scrollTo({ top: 0, behavior: 'smooth' });
}

function filterByCategory(category) {
    APP_STATE.currentCategory = category === 'all-products' ? 'all' : category;
    APP_STATE.currentPage = 1;
    renderCategories();
    renderProducts();
}

function setupSearch() {
    const searchInput = document.getElementById('searchInput');
    if (!searchInput) return;

    searchInput.addEventListener('input', (e) => {
        APP_STATE.searchQuery = e.target.value;
        APP_STATE.currentPage = 1;
        renderProducts();
    });
}

// ===== PRODUCT DETAIL PAGE =====
function renderProductDetail(productId) {
    const product = APP_STATE.products.find(p => p.id === parseInt(productId));

    if (!product) {
        render404Page();
        return;
    }

    const app = document.getElementById('mainApp');

    // Get related products
    const relatedProducts = APP_STATE.products
        .filter(p => p.category === product.category && p.id !== product.id)
        .slice(0, 3);

    app.innerHTML = `
        <div class="main-content">
            <div class="container">
                <div class="product-detail fade-in">
                    <div class="detail-grid">
                        <div class="image-gallery">
                            <img src="${product.image}" alt="${product.title}" class="main-image">
                        </div>

                        <div class="detail-content">
                            <span class="product-category">${product.category}</span>
                            <h1>${product.title}</h1>
                            <p>${product.fullDescription}</p>

                            <h3>Key Features</h3>
                            <ul class="features-list">
                                ${product.features.map(f => `<li>${f}</li>`).join('')}
                            </ul>

                            ${product.price ? `<h2 style="margin-top: 1.5rem;">${product.price}</h2>` : ''}

                            <div class="affiliate-buttons">
                                ${product.affiliateLinks.amazon ? `<a href="${product.affiliateLinks.amazon}" target="_blank" class="btn btn-affiliate">Buy on Amazon</a>` : ''}
                                ${product.affiliateLinks.flipkart ? `<a href="${product.affiliateLinks.flipkart}" target="_blank" class="btn btn-affiliate">Buy on Flipkart</a>` : ''}
                            </div>
                        </div>
                    </div>

                    ${relatedProducts.length > 0 ? `
                        <div class="related-products">
                            <h3>Related Products</h3>
                            <div class="products-grid">
                                ${relatedProducts.map(p => `
                                    <div class="product-card" onclick="navigateTo('product', ${p.id})">
                                        <img src="${p.image}" alt="${p.title}" class="product-image">
                                        <div class="product-info">
                                            <span class="product-category">${p.category}</span>
                                            <h3 class="product-title">${p.title}</h3>
                                            <p class="product-description">${p.shortDescription}</p>
                                            <button class="btn btn-secondary">View Details</button>
                                        </div>
                                    </div>
                                `).join('')}
                            </div>
                        </div>
                    ` : ''}
                </div>
            </div>
        </div>
    `;
}

