// ===== PRODUCT MANAGEMENT =====
function showAddProductModal() {
    showProductModal();
}

function showProductModal(productId = null) {
    const product = productId ? APP_STATE.products.find(p => p.id === productId) : null;
    const isEdit = !!product;

    const modal = document.getElementById('productModal');
    const modalBody = document.getElementById('modalBody');

    modalBody.innerHTML = `
        <form id="productForm" onsubmit="handleProductSubmit(event, ${isEdit ? productId : 'null'})">
            <div class="form-group">
                <label class="form-label">Product Title</label>
                <input type="text" class="form-input" id="productTitle" value="${product?.title || ''}" required>
            </div>

            <div class="form-group">
                <label class="form-label">Category</label>
                <select class="form-select" id="productCategory" required>
                    ${CATEGORIES.slice(1).map(cat => `
                        <option value="${cat}" ${product?.category === cat ? 'selected' : ''}>${cat}</option>
                    `).join('')}
                </select>
            </div>

            <div class="form-group">
                <label class="form-label">Image URL</label>
                <input type="url" class="form-input" id="productImage" value="${product?.image || ''}" required>
            </div>

            <div class="form-group">
                <label class="form-label">Short Description</label>
                <textarea class="form-textarea" id="productShortDesc" required>${product?.shortDescription || ''}</textarea>
            </div>

            <div class="form-group">
                <label class="form-label">Full Description</label>
                <textarea class="form-textarea" id="productFullDesc" required>${product?.fullDescription || ''}</textarea>
            </div>

            <div class="form-group">
                <label class="form-label">Features (one per line)</label>
                <textarea class="form-textarea" id="productFeatures">${product?.features?.join('\n') || ''}</textarea>
            </div>

            <div class="form-group">
                <label class="form-label">Price (optional)</label>
                <input type="text" class="form-input" id="productPrice" value="${product?.price || ''}">
            </div>

            <div class="form-group">
                <label class="form-label">Amazon Link</label>
                <input type="url" class="form-input" id="amazonLink" value="${product?.affiliateLinks?.amazon || ''}">
            </div>

            <div class="form-group">
                <label class="form-label">Flipkart Link</label>
                <input type="url" class="form-input" id="flipkartLink" value="${product?.affiliateLinks?.flipkart || ''}">
            </div>

            <button type="submit" class="btn btn-primary" style="width: 100%;">${isEdit ? 'Update' : 'Add'} Product</button>
        </form>
    `;

    modal.classList.add('active');
}

function handleProductSubmit(e, productId) {
    e.preventDefault();

    const productData = {
        id: productId || Date.now(),
        title: document.getElementById('productTitle').value,
        category: document.getElementById('productCategory').value,
        image: document.getElementById('productImage').value,
        shortDescription: document.getElementById('productShortDesc').value,
        fullDescription: document.getElementById('productFullDesc').value,
        features: document.getElementById('productFeatures').value.split('\n').filter(f => f.trim()),
        price: document.getElementById('productPrice').value,
        affiliateLinks: {
            amazon: document.getElementById('amazonLink').value,
            flipkart: document.getElementById('flipkartLink').value
        }
    };

    if (productId) {
        // Update existing product
        const index = APP_STATE.products.findIndex(p => p.id === productId);
        APP_STATE.products[index] = productData;
    } else {
        // Add new product
        APP_STATE.products.unshift(productData);
    }

    saveProducts();
    closeModal();
    renderAdminDashboard();
    alert('Product saved successfully!');
}

function editProduct(productId) {
    showProductModal(productId);
}

function deleteProduct(productId) {
    if (confirm('Are you sure you want to delete this product?')) {
        APP_STATE.products = APP_STATE.products.filter(p => p.id !== productId);
        saveProducts();
        renderAdminDashboard();
    }
}

// ===== LEGAL PAGES =====
function renderPrivacyPage() {
    const app = document.getElementById('mainApp');
    app.innerHTML = `
        <div class="main-content">
            <div class="container">
                <div class="section contact-section fade-in">
                    <h1>Privacy Policy</h1>
                    <p>Last updated: February 10, 2026</p>
                    <p>Your privacy is important to us. This Privacy Policy explains how we collect, use, and protect your information...</p>
                    <h3>Information We Collect</h3>
