// ===== MODAL FUNCTIONS =====
function closeModal() {
    document.getElementById('productModal').classList.remove('active');
}

// ===== HEADER SCROLL EFFECT =====
window.addEventListener('scroll', () => {
    const header = document.getElementById('header');
    if (window.scrollY > 50) {
        header.classList.add('scrolled');
    } else {
        header.classList.remove('scrolled');
    }
});

// ===== MOBILE MENU =====
document.getElementById('mobileMenuToggle').addEventListener('click', () => {
    const menu = document.getElementById('navMenu');
    const toggle = document.getElementById('mobileMenuToggle');
    menu.classList.toggle('active');
    toggle.classList.toggle('active');
});

// ===== NAVIGATION EVENTS =====
document.getElementById('navHome').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('home');
});

document.getElementById('navCategories').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('home');
    setTimeout(() => {
        document.querySelector('.sidebar')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
});

document.getElementById('navAbout').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('about');
});

document.getElementById('navBlog').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('blog');
});

document.getElementById('navContact').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('contact');
});

document.getElementById('navAdmin').addEventListener('click', (e) => {
    e.preventDefault();
    navigateTo('admin');
});

document.getElementById('closeModal').addEventListener('click', closeModal);

// Close modal on outside click
document.getElementById('productModal').addEventListener('click', (e) => {
    if (e.target.id === 'productModal') {
        closeModal();
    }
});

// ===== FOOTER LINKS =====
document.addEventListener('click', (e) => {
    if (e.target.matches('a[href="#privacy"]')) {
        e.preventDefault();
        navigateTo('privacy');
    } else if (e.target.matches('a[href="#disclaimer"]')) {
        e.preventDefault();
        navigateTo('disclaimer');
    } else if (e.target.matches('a[href="#terms"]')) {
        e.preventDefault();
        navigateTo('terms');
    }
});

// ===== INITIALIZATION =====
function init() {
    initializeStorage();
    APP_STATE.isAdmin = checkAuth();
    navigateTo('home');
}

// Start the app
init();

